package whileloop;

public class Whileloop5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int count =0;
		for(int i=1;i<=20;i++)
		{
			if(i%2==0)
			{
				count++;
				
			}
		}
		System.out.println("count of even numbers from 1 to 20:"+count);

	}

}
